    <script>
    function getMsg(){
        var slogan = [<?php $this->options->slogan_list(); ?>];
    	var r = random(0,slogan.length-1);
    	$("#slogan").html(slogan[r])
    }
    
    // ‰Ω†ÈÉΩÊù•ËøôÈáåÁøª‰ª£Á†ÅÁúãÊâÄÊúâÊ†áÁ≠æ‰∫ÜÔºåÁúãÊù•ÊòØÁúüÁà±„ÄÇ
    var tags = [<?php $this->options->tags_list(); ?>];
    
    let rollTimer = null;
    
    function random(Min,Max){
          var Range = Max - Min;
          var Rand = Math.random();
          var num = Min + Math.round(Rand * Range);
          return num;
    }
    
    $(function(){
      getMsg();
    //   console.log('BN & SY\'s Love');
    })
    
    function rollATag() {
      $(".roll-tag").addClass('active');
      let el = $(".roll-tag span.ready")
      el.addClass('removing');
      setTimeout(() => {
        el.remove();
      }, 100)
      var span = $("<span></span>").text(getRandomTag());
      $(".roll-tag").append(span);
      setTimeout(() => {
        span.addClass('ready');
      }, 100)
    }
    
    function rollOnce() {
      clearInterval(rollTimer)
        rollTimer = setInterval(rollATag, 20)
      setTimeout(function() {
        clearInterval(rollTimer)
        rollTimer = setInterval(rollATag, 40)
      }, 400)
      setTimeout(function() {
        clearInterval(rollTimer)
        rollTimer = setInterval(rollATag, 80)
      }, 800)
      setTimeout(function() {
        clearInterval(rollTimer)
        rollTimer = setInterval(rollATag, 140)
      }, 1200)
      setTimeout(function() {
        clearInterval(rollTimer)
        rollTimer = setInterval(rollATag, 240)
      }, 1600)
      setTimeout(function() {
        clearInterval(rollTimer)
        rollTimer = null
      }, 1800)
    }
    
    function getRandomTag() {
      return tags[random(0, tags.length-1)];
    }
    
    function secondToDate(second) {
        if (!second) {
            return 0;
        }
        var time = new Array(0, 0, 0, 0, 0);
        if (second >= 365 * 24 * 3600) {
            time[0] = parseInt(second / (365 * 24 * 3600));
            second %= 365 * 24 * 3600;
        }
        if (second >= 24 * 3600) {
            time[1] = parseInt(second / (24 * 3600));
            second %= 24 * 3600;
        }
        if (second >= 3600) {
            time[2] = parseInt(second / 3600);
            second %= 3600;
        }
        if (second >= 60) {
            time[3] = parseInt(second / 60);
            second %= 60;
        }
        if (second > 0) {
            time[4] = second;
        }
        return time;
    }
    
    function setTime() {
        var know_time = Math.round(new Date("<?php $this->options->knowTime(); ?>").getTime() / 1000);
        var love_time = Math.round(new Date("<?php $this->options->loveTime(); ?>").getTime() / 1000);
        var meet_time = Math.round(new Date("<?php $this->options->meetTime(); ?>").getTime() / 1000);
        var timestamp = Math.round(new Date().getTime() / 1000);
        duration = secondToDate((timestamp - know_time));
        durationHtml = duration[0] + ' Âπ¥ ' + duration[1] + ' Â§© ' + duration[2] + ' Êó∂ ' + duration[3] + ' ÂàÜ ' + duration[4] + ' Áßí';
        document.getElementById("know_time_to_now").innerHTML = durationHtml;
        duration = secondToDate((timestamp - love_time));
        durationHtml = duration[0] + ' Âπ¥ ' + duration[1] + ' Â§© ' + duration[2] + ' Êó∂ ' + duration[3] + ' ÂàÜ ' + duration[4] + ' Áßí';
        document.getElementById("love_time_to_now").innerHTML = durationHtml;
        duration = secondToDate((timestamp - meet_time));
        durationHtml = duration[0] + ' Âπ¥ ' + duration[1] + ' Â§© ' + duration[2] + ' Êó∂ ' + duration[3] + ' ÂàÜ ' + duration[4] + ' Áßí';
        document.getElementById("meet_time_to_now").innerHTML = durationHtml;
    }
    setInterval(setTime, 1000);
    </script>
